import React, { Dispatch, FC, SetStateAction } from 'react';
import { FormControl, FormLabel, RadioGroup, Radio, FormControlLabel } from '@mui/material';
import { Controller } from 'react-hook-form';

import { IAnswer } from '../../../consts';

import './style.css';

interface IProps {
  answers: IAnswer[];
  name: string;
  control: any;
  id: number;
  setError: Dispatch<SetStateAction<boolean>>;
}

const FormControlAnswers: FC<IProps> = ({ answers, name, control, id, setError }) => {
  return (
    <FormControl>
      <FormLabel color="primary">{name}</FormLabel>
      <div>
        <div className="bod">
          <div className="inputBox">
            <div className="leftObject">
              <Controller
                rules={{ required: true }}
          control={control}
          name={`question${id + 1}`}
          render={({ field }) => {
            return (
              <RadioGroup {...field} onClick={() => setError(false)}>
                {answers.map((answer) => {
                  return (
                    <FormControlLabel
                      color="primary"
                      value={`${answer.name}_${answer.isTrue}`}
                      control={<Radio />}
                      label={answer.name}
                    />
                  );
                })}
              </RadioGroup>
            );
          }}
        />
            <img src="" alt="Image" className="rightImage" />
            </div>
          </div>
        </div>
    </div>
    </FormControl>
  );
};

export default FormControlAnswers;
